  `DC_HIT` int(10) unsigned DEFAULT '0',
  `DC_MEMO1` varchar(100) DEFAULT NULL,
  `DC_MEMO2` varchar(100) DEFAULT NULL,
  `DC_CODE` varchar(100) DEFAULT NULL,
  `DC_COUNTRY` varchar(100) DEFAULT NULL,
  `DC_DT_COLLECT_STR` varchar(50) DEFAULT NULL,
  PRIMARY KEY (`IDX`)
) ENGINE=InnoDB AUTO_INCREMENT=3005 DEFAULT CHARSET=utf8;
